import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { PieChart, Pie, Cell } from 'recharts'; // Add this import
import axios from 'axios';
import './App.css';

// Update the API_BASE_URL constant
const API_BASE_URL = 'http://localhost:5030/api';

function App() {
  const [selectedMonth, setSelectedMonth] = useState(3);
  const [searchQuery, setSearchQuery] = useState('');
  const [transactions, setTransactions] = useState([]);
  const [statistics, setStatistics] = useState({
    totalSaleAmount: 0,
    totalSoldItems: 0,
    totalNotSoldItems: 0,
  });
  const [barChartData, setBarChartData] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [pieChartData, setPieChartData] = useState([]); // Add this state
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8']; // Add colors for pie chart

  const months = [
    { value: 1, label: 'January' },
    { value: 2, label: 'February' },
    { value: 3, label: 'March' },
    { value: 4, label: 'April' },
    { value: 5, label: 'May' },
    { value: 6, label: 'June' },
    { value: 7, label: 'July' },
    { value: 8, label: 'August' },
    { value: 9, label: 'September' },
    { value: 10, label: 'October' },
    { value: 11, label: 'November' },
    { value: 12, label: 'December' },
  ];

  // Update the fetchData function's pie chart section
  const fetchData = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/combined-data`, {
        params: {
          month: selectedMonth,
          search: searchQuery,
          page,
          perPage: 10
        }
      });

      if (response.data) {
        setTransactions(response.data.transactions.transactions || []);
        setTotalPages(Math.ceil(response.data.transactions.total / 10));
        setStatistics(response.data.statistics || {
          totalSaleAmount: 0,
          totalSoldItems: 0,
          totalNotSoldItems: 0
        });
        setBarChartData(response.data.barChart || []);
        setPieChartData(response.data.pieChart || []);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('Error loading data. Please try again.');
    }
  };

  useEffect(() => {
    fetchData();
  }, [selectedMonth, searchQuery, page]);

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
    setPage(1);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setPage(1);
  };

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  return (
    <Container 
      maxWidth="lg" 
      sx={{ 
        py: 4,
        minHeight: '100vh',
        background: 'linear-gradient(180deg, #000000 0%, #1a1a1a 100%)',
      }}
    >
      {/* Add Navbar */}
      <Box
        sx={{
          position: 'fixed',
          top: '10px',
          left: 0,
          right: 0,
          zIndex: 50,
          backgroundColor: 'rgba(0, 0, 0, 0.3)', // Changed from 0.6 to 0.3 for more transparency
          backdropFilter: 'blur(12px)', // Increased blur effect to maintain readability
          border: '1px solid rgba(255, 255, 255, 0.1)', // Added subtle border
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', // Added subtle shadow
        }}
      >
        <Container maxWidth="lg" sx={{ py: 1 }}>
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'center', // Changed from 'space-between' to 'center'
              alignItems: 'center',
              height: '64px',
              width: '100%', // Added width
            }}
          >
            <Typography 
              variant="h4" 
              component="h1"
              sx={{
                fontSize: '2.5rem',
                fontWeight: 'bold',
                fontFamily: 'Montserrat, sans-serif',
                background: 'linear-gradient(90deg, #7a56d6, #ffffff, #7a56d6, #ffffff)',
                backgroundSize: '300% 100%',
                WebkitBackgroundClip: 'text',
                WebkitFontSmoothing: 'antialiased',
                WebkitTextFillColor: 'transparent',
                animation: 'gradient 8s ease infinite',
                textAlign: 'center', // Added text alignment
              }}
            >
              Roxiler Product Management
            </Typography>
          </Box>
        </Container>
      </Box>

      {/* Add margin-top to content to account for fixed navbar */}
      <Box sx={{ mt: 12 }}>
        {/* Rest of your existing content */}
        {/* Remove the original Typography title since it's now in navbar */}

        {/* Month Selection and Search */}
        <Box sx={{ mb: 4, display: 'flex', gap: 2 }}>
          <FormControl sx={{ minWidth: 200 }}>
            <InputLabel>Select Month</InputLabel>
            <Select
              value={selectedMonth}
              label="Select Month"
              onChange={handleMonthChange}
            >
              {months.map((month) => (
                <MenuItem key={month.value} value={month.value}>
                  {month.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            label="Search Transactions"
            variant="outlined"
            value={searchQuery}
            onChange={handleSearchChange}
            sx={{ flexGrow: 1 }}
          />
        </Box>

        {/* Statistics Cards */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography color="textSecondary" gutterBottom>
                  Total Sale Amount
                </Typography>
                <Typography variant="h5">
                  ₹{statistics.totalSaleAmount.toLocaleString()}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography color="textSecondary" gutterBottom>
                  Total Sold Items
                </Typography>
                <Typography variant="h5">
                  {statistics.totalSoldItems}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography color="textSecondary" gutterBottom>
                  Total Not Sold Items
                </Typography>
                <Typography variant="h5">
                  {statistics.totalNotSoldItems}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        {/* Transactions Table */}
        <TableContainer component={Paper} sx={{ mb: 4 }} className="transactions-table">
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Image</TableCell>
                <TableCell>Title</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Category</TableCell>
                <TableCell>Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {Array.isArray(transactions) && transactions.length > 0 ? (
                transactions.map((transaction) => (
                  <TableRow key={transaction.id || transaction._id}>
                    <TableCell>{transaction.id}</TableCell>
                    <TableCell>
                      <img
                        src={transaction.image}
                        alt={transaction.title}
                        style={{
                          width: '50px',
                          height: '50px',
                          objectFit: 'cover',
                          borderRadius: '4px'
                        }}
                      />
                    </TableCell>
                    <TableCell>{transaction.title}</TableCell>
                    <TableCell>{transaction.description}</TableCell>
                    <TableCell>₹{(transaction.price || 0).toLocaleString()}</TableCell>
                    <TableCell>{transaction.category}</TableCell>
                    <TableCell>{transaction.sold ? 'Sold' : 'Not Sold'}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={7} align="center">
                    No transactions found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Pagination */}
        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePageChange}
            color="primary"
          />
        </Box>

        {/* Bar Chart */}
        <Paper sx={{ p: 2, mb: 4 }}>
          <Typography variant="h6" gutterBottom>
            Price Range Distribution
          </Typography>
          <Box sx={{ height: 400 }}>
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={barChartData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="range" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="count" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
          </Box>
        </Paper>

        {/* Add Pie Chart */}
        <Paper sx={{ p: 2, mb: 4 }}>
          <Typography variant="h6" gutterBottom>
            Category Distribution
          </Typography>
          <Box sx={{ height: 400 }}>
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={pieChartData}
                  dataKey="value"
                  nameKey="name"
                  cx="50%"
                  cy="50%"
                  outerRadius={120}
                  innerRadius={0}
                  paddingAngle={5}
                  label
                >
                  {pieChartData.map((entry, index) => (
                    <Cell 
                      key={`cell-${index}`} 
                      fill={COLORS[index % COLORS.length]} 
                    />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => `${value} items`} />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
}

export default App;
