import React from 'react';
import { Box, Container, Typography, AppBar, Toolbar } from '@mui/material';
import StorefrontIcon from '@mui/icons-material/Storefront';

const Navbar = () => (
  <AppBar
    position="fixed"
    sx={{
      background: 'rgba(0, 0, 0, 0.8)',
      backdropFilter: 'blur(8px)',
      borderBottom: '1px solid rgba(122, 86, 214, 0.2)',
      boxShadow: '0 4px 30px rgba(122, 86, 214, 0.1)',
    }}
  >
    <Container maxWidth="lg">
      <Toolbar sx={{ justifyContent: 'center', height: '70px' }}>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center',
          gap: 2,
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            left: -20,
            right: -20,
            height: '2px',
            bottom: -10,
            background: 'linear-gradient(90deg, transparent, #7a56d6, transparent)',
          }
        }}>
          <StorefrontIcon 
            sx={{ 
              fontSize: 40, 
              color: '#7a56d6',
              filter: 'drop-shadow(0 0 8px rgba(122, 86, 214, 0.5))'
            }} 
          />
          <Typography 
            variant="h4" 
            component="h1"
            sx={{
              fontSize: { xs: '1.8rem', md: '2.5rem' },
              fontWeight: 'bold',
              fontFamily: 'Montserrat, sans-serif',
              background: 'linear-gradient(90deg, #7a56d6, #ffffff)',
              backgroundSize: '200% auto',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              animation: 'gradient 6s ease infinite',
              '@keyframes gradient': {
                '0%': { backgroundPosition: '0% center' },
                '50%': { backgroundPosition: '100% center' },
                '100%': { backgroundPosition: '0% center' }
              }
            }}
          >
            Roxiler Product Management
          </Typography>
        </Box>
      </Toolbar>
    </Container>
  </AppBar>
);

export default Navbar;
