import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { PieChart, Pie, Cell } from 'recharts'; // Add this import
import axios from 'axios';
import './App.css';

// Update the API_BASE_URL constant
const API_BASE_URL = 'http://localhost:5030/api';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8']; // Add colors for pie chart

import Navbar from './components/Navbar';
import SearchBar from './components/SearchBar';
import StatisticsCards from './components/StatisticsCards';
import TransactionsTable from './components/TransactionsTable';
import Charts from './components/Charts';

const months = [
  { value: 1, label: 'January' }, { value: 2, label: 'February' },
  { value: 3, label: 'March' }, { value: 4, label: 'April' },
  { value: 5, label: 'May' }, { value: 6, label: 'June' },
  { value: 7, label: 'July' }, { value: 8, label: 'August' },
  { value: 9, label: 'September' }, { value: 10, label: 'October' },
  { value: 11, label: 'November' }, { value: 12, label: 'December' }
];

function App() {
  const [selectedMonth, setSelectedMonth] = useState(3);
  const [searchQuery, setSearchQuery] = useState('');
  const [transactions, setTransactions] = useState([]);
  const [statistics, setStatistics] = useState({
    totalSaleAmount: 0,
    totalSoldItems: 0,
    totalNotSoldItems: 0,
  });
  const [barChartData, setBarChartData] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [pieChartData, setPieChartData] = useState([]);

  const fetchData = async () => {
    try {
      const { data } = await axios.get(`${API_BASE_URL}/combined-data`, {
        params: { month: selectedMonth, search: searchQuery, page, perPage: 10 }
      });

      if (data) {
        setTransactions(data.transactions.transactions || []);
        setTotalPages(Math.ceil(data.transactions.total / 10));
        setStatistics(data.statistics || {
          totalSaleAmount: 0,
          totalSoldItems: 0,
          totalNotSoldItems: 0
        });
        setBarChartData(data.barChart || []);
        setPieChartData(data.pieChart || []);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('Error loading data. Please try again.');
    }
  };

  useEffect(() => {
    fetchData();
  }, [selectedMonth, searchQuery, page]);

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
    setPage(1);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setPage(1);
  };

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  return (
    <Container maxWidth="lg" sx={{ 
      py: 4,
      minHeight: '100vh',
      background: 'linear-gradient(180deg, #000000 0%, #1a1a1a 100%)',
    }}>
      <Navbar />

      <Box sx={{ mt: 12 }}>
        <SearchBar
          selectedMonth={selectedMonth}
          searchQuery={searchQuery}
          months={months}
          onMonthChange={handleMonthChange}
          onSearchChange={handleSearchChange}
        />

        <StatisticsCards statistics={statistics} />

        <TransactionsTable transactions={transactions} />

        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePageChange}
            color="primary"
          />
        </Box>

        <Charts barChartData={barChartData} pieChartData={pieChartData} />
      </Box>
    </Container>
  );
}

export default App;
