import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { PieChart, Pie, Cell } from 'recharts'; // Add this import
import axios from 'axios';
import Dashboard from './components/Dashboard';

// Update the API_BASE_URL constant
const API_BASE_URL = 'http://localhost:5030/api';

function App() {
  const [selectedMonth, setSelectedMonth] = useState(3);
  const [searchQuery, setSearchQuery] = useState('');
  const [transactions, setTransactions] = useState([]);
  const [statistics, setStatistics] = useState({
    totalSaleAmount: 0,
    totalSoldItems: 0,
    totalNotSoldItems: 0,
  });
  const [barChartData, setBarChartData] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [pieChartData, setPieChartData] = useState([]); // Add this state
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8']; // Add colors for pie chart

  const months = [
    { value: 1, label: 'January' },
    { value: 2, label: 'February' },
    { value: 3, label: 'March' },
    { value: 4, label: 'April' },
    { value: 5, label: 'May' },
    { value: 6, label: 'June' },
    { value: 7, label: 'July' },
    { value: 8, label: 'August' },
    { value: 9, label: 'September' },
    { value: 10, label: 'October' },
    { value: 11, label: 'November' },
    { value: 12, label: 'December' },
  ];

  // Update the fetchData function's pie chart section
  const fetchData = async () => {
    try {
      const transactionsResponse = await axios.get(`${API_BASE_URL}/transactions`, {
        params: {
          month: selectedMonth,
          search: searchQuery,
          page,
          perPage: 10,
        },
      });

      // Fix: Check if data exists and has the expected structure
      if (transactionsResponse.data && transactionsResponse.data.transactions) {
        setTransactions(transactionsResponse.data.transactions);
        setTotalPages(Math.ceil(transactionsResponse.data.total / 10));
      }

      const statisticsResponse = await axios.get(`${API_BASE_URL}/statistics`, {
        params: { month: selectedMonth },
      });

      // Fix: Check if statistics data exists
      if (statisticsResponse.data) {
        setStatistics({
          totalSaleAmount: statisticsResponse.data.totalSaleAmount || 0,
          totalSoldItems: statisticsResponse.data.totalSoldItems || 0,
          totalNotSoldItems: statisticsResponse.data.totalNotSoldItems || 0,
        });
      }

      const barChartResponse = await axios.get(`${API_BASE_URL}/bar-chart`, {
        params: { month: selectedMonth },
      });

      // Fix: Check if bar chart data exists
      if (barChartResponse.data) {
        setBarChartData(barChartResponse.data);
      }

      // Add pie chart data fetch
      // Update pie chart data fetch and processing
      // In the fetchData function, update the pie chart section:
      const pieChartResponse = await axios.get(`${API_BASE_URL}/pie-chart`, {
        params: { month: selectedMonth },
      });

      if (pieChartResponse.data) {
        setPieChartData(pieChartResponse.data); // No transformation needed now
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('Error loading data. Please try again.');
    }
  };

  useEffect(() => {
    fetchData();
  }, [selectedMonth, searchQuery, page]);

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
    setPage(1);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setPage(1);
  };

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Roxiler Product Management
      </Typography>

      {/* Month Selection and Search */}
      <Box sx={{ mb: 4, display: 'flex', gap: 2 }}>
        <FormControl sx={{ minWidth: 200 }}>
          <InputLabel>Select Month</InputLabel>
          <Select
            value={selectedMonth}
            label="Select Month"
            onChange={handleMonthChange}
          >
            {months.map((month) => (
              <MenuItem key={month.value} value={month.value}>
                {month.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <TextField
          label="Search Transactions"
          variant="outlined"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{ flexGrow: 1 }}
        />
      </Box>

      {/* Statistics Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Sale Amount
              </Typography>
              <Typography variant="h5">
                ₹{statistics.totalSaleAmount.toLocaleString()}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Sold Items
              </Typography>
              <Typography variant="h5">
                {statistics.totalSoldItems}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Not Sold Items
              </Typography>
              <Typography variant="h5">
                {statistics.totalNotSoldItems}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Transactions Table */}
      <TableContainer component={Paper} sx={{ mb: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Title</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {transactions && transactions.length > 0 ? (
              transactions.map((transaction) => (
                <TableRow key={transaction.id || transaction._id}>
                  <TableCell>{transaction.id}</TableCell>
                  <TableCell>{transaction.title}</TableCell>
                  <TableCell>{transaction.description}</TableCell>
                  <TableCell>₹{(transaction.price || 0).toLocaleString()}</TableCell>
                  <TableCell>{transaction.category}</TableCell>
                  <TableCell>{transaction.sold ? 'Sold' : 'Not Sold'}</TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  No transactions found
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Pagination */}
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
        <Pagination
          count={totalPages}
          page={page}
          onChange={handlePageChange}
          color="primary"
        />
      </Box>

      {/* Bar Chart */}
      <Paper sx={{ p: 2, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Price Range Distribution
        </Typography>
        <Box sx={{ height: 400 }}>
          <ResponsiveContainer width="100%" height="100%">
            <BarChart data={barChartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="range" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="count" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </Box>
      </Paper>

      {/* Add Pie Chart */}
      <Paper sx={{ p: 2, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Category Distribution
        </Typography>
        <Box sx={{ height: 400 }}>
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={pieChartData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={120}
                innerRadius={0}
                paddingAngle={5}
                label
              >
                {pieChartData.map((entry, index) => (
                  <Cell 
                    key={`cell-${index}`} 
                    fill={COLORS[index % COLORS.length]} 
                  />
                ))}
              </Pie>
              <Tooltip formatter={(value) => `${value} items`} />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </Box>
      </Paper>
    </Container>
  );
}

export default App;
