import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { PieChart, Pie, Cell } from 'recharts'; // Add this import
import axios from 'axios';
import './App.css';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// Update the API_BASE_URL constant
const API_BASE_URL = 'http://localhost:5030/api';

// Add dark theme configuration
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#9c27b0',
    },
    secondary: {
      main: '#ba68c8',
    },
    background: {
      default: '#121212',
      paper: '#1e1e1e',
    },
  },
});

function App() {
  const [selectedMonth, setSelectedMonth] = useState(3);
  const [searchQuery, setSearchQuery] = useState('');
  const [transactions, setTransactions] = useState([]);
  const [statistics, setStatistics] = useState({
    totalSaleAmount: 0,
    totalSoldItems: 0,
    totalNotSoldItems: 0,
  });
  const [barChartData, setBarChartData] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [pieChartData, setPieChartData] = useState([]); // Add this state
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8']; // Add colors for pie chart

  const months = [
    { value: 1, label: 'January' },
    { value: 2, label: 'February' },
    { value: 3, label: 'March' },
    { value: 4, label: 'April' },
    { value: 5, label: 'May' },
    { value: 6, label: 'June' },
    { value: 7, label: 'July' },
    { value: 8, label: 'August' },
    { value: 9, label: 'September' },
    { value: 10, label: 'October' },
    { value: 11, label: 'November' },
    { value: 12, label: 'December' },
  ];

  // Update the fetchData function's pie chart section
  const fetchData = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/combined-data`, {
        params: {
          month: selectedMonth,
          search: searchQuery,
          page,
          perPage: 10
        }
      });

      if (response.data) {
        setTransactions(response.data.transactions.transactions || []);
        setTotalPages(Math.ceil(response.data.transactions.total / 10));
        setStatistics(response.data.statistics || {
          totalSaleAmount: 0,
          totalSoldItems: 0,
          totalNotSoldItems: 0
        });
        setBarChartData(response.data.barChart || []);
        setPieChartData(response.data.pieChart || []);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('Error loading data. Please try again.');
    }
  };

  useEffect(() => {
    fetchData();
  }, [selectedMonth, searchQuery, page]);

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
    setPage(1);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setPage(1);
  };

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <Container maxWidth="lg" sx={{ 
        py: 4, 
        backgroundColor: '#121212',
        minHeight: '100vh',
        color: 'white'
      }}>
        <Typography variant="h4" component="h1" gutterBottom sx={{ 
          color: '#9c27b0',
          textAlign: 'center',
          mb: 4
        }}>
          Roxiler Product Management
        </Typography>

        {/* Month Selection and Search */}
        <Box sx={{ 
          mb: 4, 
          display: 'flex', 
          gap: 2,
          backgroundColor: '#1e1e1e',
          p: 3,
          borderRadius: 2,
          border: '1px solid #2d2d2d'
        }}>
          <FormControl sx={{ minWidth: 200 }}>
            <InputLabel>Select Month</InputLabel>
            <Select
              value={selectedMonth}
              label="Select Month"
              onChange={handleMonthChange}
            >
              {months.map((month) => (
                <MenuItem key={month.value} value={month.value}>
                  {month.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            label="Search Transactions"
            variant="outlined"
            value={searchQuery}
            onChange={handleSearchChange}
            sx={{ flexGrow: 1 }}
          />
        </Box>

        {/* Statistics Cards */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} md={4}>
            <Card sx={{
              backgroundColor: '#1e1e1e',
              border: '1px solid #2d2d2d',
              transition: 'all 0.3s ease',
              '&:hover': {
                transform: 'translateY(-5px)',
                borderColor: '#9c27b0',
                boxShadow: '0 0 15px rgba(156, 39, 176, 0.3)'
              }
            }}>
              <CardContent>
                <Typography color="textSecondary" gutterBottom>
                  Total Sale Amount
                </Typography>
                <Typography variant="h5">
                  ₹{statistics.totalSaleAmount.toLocaleString()}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        {/* Transactions Table */}
        <TableContainer component={Paper} sx={{ 
          mb: 4,
          backgroundColor: '#1e1e1e',
          border: '1px solid #2d2d2d'
        }}>
          <Table>
            <TableHead>
              <TableRow sx={{ backgroundColor: '#2d2d2d' }}>
                <TableCell>ID</TableCell>
                <TableCell>Image</TableCell>
                <TableCell>Title</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Category</TableCell>
                <TableCell>Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {Array.isArray(transactions) && transactions.length > 0 ? (
                transactions.map((transaction) => (
                  <TableRow key={transaction.id || transaction._id}>
                    <TableCell>{transaction.id}</TableCell>
                    <TableCell>
                      <img
                        src={transaction.image}
                        alt={transaction.title}
                        style={{
                          width: '50px',
                          height: '50px',
                          objectFit: 'cover',
                          borderRadius: '4px'
                        }}
                      />
                    </TableCell>
                    <TableCell>{transaction.title}</TableCell>
                    <TableCell>{transaction.description}</TableCell>
                    <TableCell>₹{(transaction.price || 0).toLocaleString()}</TableCell>
                    <TableCell>{transaction.category}</TableCell>
                    <TableCell>{transaction.sold ? 'Sold' : 'Not Sold'}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={7} align="center">
                    No transactions found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Charts */}
        <Paper sx={{ 
          p: 2, 
          mb: 4,
          backgroundColor: '#1e1e1e',
          border: '1px solid #2d2d2d'
        }}>
          <Typography variant="h6" gutterBottom>
            Price Range Distribution
          </Typography>
          <Box sx={{ height: 400 }}>
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={barChartData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="range" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="count" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
          </Box>
        </Paper>

        <Paper sx={{ 
          p: 2, 
          mb: 4,
          backgroundColor: '#1e1e1e',
          border: '1px solid #2d2d2d'
        }}>
          <Typography variant="h6" gutterBottom>
            Category Distribution
          </Typography>
          <Box sx={{ height: 400 }}>
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={pieChartData}
                  dataKey="value"
                  nameKey="name"
                  cx="50%"
                  cy="50%"
                  outerRadius={120}
                  innerRadius={0}
                  paddingAngle={5}
                  label
                >
                  {pieChartData.map((entry, index) => (
                    <Cell 
                      key={`cell-${index}`} 
                      fill={COLORS[index % COLORS.length]} 
                    />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => `${value} items`} />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </Box>
        </Paper>

        {/* Pagination */}
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          mb: 4 
        }}>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePageChange}
            color="primary"
            sx={{
              '& .MuiPaginationItem-root': {
                color: 'white',
              },
              '& .Mui-selected': {
                backgroundColor: '#9c27b0 !important',
              }
            }}
          />
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
